package pekan1;

public class Membuat_Kue {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		makeBatter();
		memanggang();
		menghias();
	}
	public static void makeBatter() {
		System.out.println("Campur bahan kue kering");
		System.out.println("Krim mentega dan gula");
		System.out.println("Aduk telurnya");
		System.out.println("Masukkan bahan kue kering");
	}
	public static void memanggang() {
		System.out.println("Setel suhu oven");
		System.out.println("Setel pengatur waktu");
		System.out.println("Masukkan kue kering ke dalam oven");
		System.out.println("Biarkan biskuit untuk dipanggang");
	}
	public static void menghias() {
		System.out.println("Campur bahan untuk di-frosting");
		System.out.println("Taburkan frosting dan taburan");
	}
}